Bash(node -e "
import fs from ''fs'';
import path from ''path'';
import os from ''os'';

const COMMANDS_DIR = path.join\(os.homedir\(\), ''.claude'', ''commands''\);
const LOCAL_COMMANDS_DIR = ''./commands'';

// 列出本地命令
const localCmds = fs.readdirSync\(LOCAL_COMMANDS_DIR\);
console.log\(''本地命令文件:'', localCmds\);

// 检查目标目录
if \(fs.existsSync\(COMMANDS_DIR\)\) {
  const installedCmds = fs.readdirSync\(COMMANDS_DIR\);
  console.log\(''已安装命令:'', installedCmds\);
} else {
  console.log\(''目标目录不存在:'', COMMANDS_DIR\);
}
")
